import React from "react"
import SelectedProduct from "./SelectedProduct"

class Main extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      selected: "",
      stage: "1",
    }
  }

  // When the user clicks a product, it is added to state and passed to App.js, the stage is moved to stage: 1
  setProduct = (product) => {
    this.setState({ selected: product })
    this.setState({ stage: "1" })
    this.props.setStage("1")
    this.props.setSelected(product)
  }

  render() {
    return (
      <div className="render-items">
        <div className="list-container">
          <div className="main-image">
            {/* Load window products onclick */}
            <a
              href="#"
              className="load-anim"
              // Pass 'window' to setProduct function to pass it to the parent state
              onClick={() => this.setProduct("windows")}
            >
              <img src="products/windows/main.jpg" className="main-img" />
              <div className="main-image-header">
                <h4>Windows</h4>
              </div>
              <div className="main-image-header-quote">
                <h5>Windows Quote</h5>
              </div>
            </a>
          </div>

          {/* Load door products onclick */}
          

          {/* Load window products onclick */}
          <div className="main-image">
            <a
              href="#"
              className="load-anim"
              // Pass 'window' to setProduct function to pass it to the parent state
              onClick={() => this.setProduct("windows")}
            >
              <img src="products/windows/main.jpg" className="main-img" />
              <div className="main-image-header">
                <h4>Windows</h4>
              </div>
              <div className="main-image-header-quote">
                <h5>Windows Quote</h5>
              </div>
            </a>
          </div>
        </div>
      </div>
    )
  }
}

export default Main
