import React from "react"
import axios from "axios"

class Quote extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      name: "",
      email: "",
      message: "",
      status: "Submit",
    }
  }

  handleBack = () => {
    this.props.setInputStage("2")
  }
  handleChange(event) {
    const field = event.target.id;
    if (field === "name") {
      this.setState({ name: event.target.value });
    } else if (field === "email") {
      this.setState({ email: event.target.value });
    } else if (field === "message") {
      this.setState({ message: event.target.value });
    }
  }

  handleSubmit(event) {
    event.preventDefault();  
    this.setState({ status: "Sending" });  
    axios({
      method: "POST",
      url: "http://localhost:5000/contact",
      data: this.state,
    }).then((response) => {
      if (response.data.status === "sent") {
        alert("Message Sent");
        this.setState({ name: "", email: "", message: "", status: "Submit" });
      } else if (response.data.status === "failed") {
        alert("Message Failed");
      }
    });
  }

  render() 


  {
    return (
      <div>
        <a className="btn_back" href="#" onClick={() => this.handleBack()}>
          Back
        </a>
        <div id="user_input">
          <div className="input-container">
            <div className="input-flex">
              <div className="input-left"></div>
              <div className="input-right">
                <div className="input-right-text">
                  <div className="quote-list">
                    <div class="quote-list-img">
                      <img src={this.props.additionalprod.img} alt=""></img>
                    </div>
                    <div class="quote-list-info">
                      <div class="quote-list-product">
                        {this.props.additionalprod.name}
                      </div>
                      <div class="quote-list-additional">
                        {this.props.additionalprod2}
                      </div>
                      <div class="quote-list-colour">{this.props.colour}</div>
                    </div>
                  </div>

                  <div class="quote-form">
                    <form method="POST" onSubmit={this.handleSubmit.bind(this)}>
                      <div>
                        <label for="name">Name:</label>
                        <input
                          type="text"
                          id="name"
                          value={this.state.name}
                          onChange={this.handleChange.bind(this)}
                        />
                      </div>

                      <div>
                        <label htmlFor="email">Email:</label>
                        <input
                          type="email"
                          id="email"
                          value={this.state.email}
                          onChange={this.handleChange.bind(this)}
                          required
                        />
                      </div>
                      <div>
                        <label htmlFor="message">Message:</label>
                        <textarea
                          id="message"
                          value={this.state.message}
                          onChange={this.handleChange.bind(this)}
                          required
                        />
                        <button type="submit">{buttonText}</button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default Quote
