import React from "react"
import axios from "axios"

class ChooseColours extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      colours: [],
      inputStage: "3",
      selectedColour: "",
    }
  }

  componentDidMount() {
    axios.get("http://localhost/tmd_media_quote_slider/server/api/colours.php").then((res) => {
      this.setState({ colours: res.data })
    })
  }

  testArray = () => {
    console.log(this.state.colours)
  }

  handleBack = () => {
    this.props.setInputStage("2")
  }

  setColour = (selectedColour) => {
    this.props.setColour(selectedColour)
    this.props.setInputStage("4")
  }

  render() {
    return (
      <div>
        <a className="btn_back" href="#" onClick={() => this.handleBack()}>
          Back
        </a>
        <div id="user_input">
          <div className="input-container">
            <div className="input-flex">
              <div className="input-left"></div>
              <div className="input-right">
                <div className="input-right-text">
                  <h1>Choose a colour</h1>
                  <div className="list-colours">
                    {this.state.colours.map((colour) => (
                      <div
                        style={{ background: colour.hex }}
                        className="colour-block"
                        onClick={() => this.setColour(colour.colour)}
                      ></div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default ChooseColours
