import React from "react"
import axios from "axios"

class Postcode extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      postcode: "",
      postcodeResult: null,
      inputStage: "2",
    }
  }

  setPostcode = (input) => {
    this.setState({ postcode: input })
    this.props.setPostcode(input)
    console.log(this.state.postcode)
  }

  handleBack = () => {
    this.props.setInputStage("0")
  }

  CheckPostcode = () => {
    axios
      .get(
        "http://api.postcodes.io/postcodes/" + this.state.postcode + "/validate"
      )
      .then((postcode) => {
        this.setState({ postcodeResult: postcode.data.result })
        if (this.state.postcodeResult) {
          this.props.setInputStage("2")
        }
      })
  }

  render() {
    function renderPostcode() {
      return <h1>test</h1>
    }
    return (
      <div>
        {/* <a className="btn_back" href="#" onClick={() => this.handleBack()}>
          Back
        </a> */}
        <div id="user_input">
          <div className="input-container">
            <div className="input-flex">
              <div className="input-left"></div>
              <div className="input-right">
                <div className="input-right-text">
                  <h1>
                    {this.state.postcodeResult == false
                      ? "Please enter a valid postcode!"
                      : null}
                  </h1>
                  <form>
                    <h1>Please enter your postcode</h1>
                    <input
                      type="text"
                      value={this.state.postcode}
                      onChange={(e) => this.setPostcode(e.target.value)}
                    />
                  </form>
                  <a href="#" onClick={() => this.CheckPostcode()}>
                    Next
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default Postcode
