import React from "react"
import Dimensions from "./userInput/Dimensions"
import Postcode from "./userInput/Postcode"
import ChooseColours from "./userInput/ChooseColours"
import Quote from "./userInput/Quote"

class UserInput extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      width: "",
      projection: "",
      inputStage: "0",
      postcode: "",
      colour: "",
    }
  }

  componentDidUpdate() {
    window.onpopstate = (e) => {
      alert("fft6f")
    }
  }
  handleWidthChange = (e) => {
    this.setState({ width: e.target.value })
    console.log(this.props.additionalprod2)
  }

  handleProjectionChange = (e) => {
    this.setState({ projection: e.target.value })
  }

  setColour = (colour) => {
    this.setState({ colour: colour })
  }

  handleBack = () => {
    // this.props.setSubProd("")
